ymaps.ready(function(){var myMap=new ymaps.Map("map",{center:[59.939238,30.329213],zoom:16,controls:[]},{searchControlProvider:"yandex#search"});myPlacemark=new ymaps.Placemark([59.938585,30.323089],{},{iconLayout:"default#image",iconImageHref:"img/map-pin.png",iconImageSize:[218,142],iconImageOffset:[-40,-140]}),myMap.geoObjects.add(myPlacemark)});var link=document.querySelector(".map__button"),popup=document.querySelector(".modal-wrapper"),close=popup.querySelector(".modal__button-close"),form=popup.querySelector("form"),name=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email2]"),isStorageSupport=!0,storage="";try{storage=localStorage.getItem("name")}catch(err){isStorageSupport=!1}link.addEventListener("click",function(evt){evt.preventDefault(),popup.classList.add("modal-show"),storage?(name.value=storage,email.focus()):name.focus()}),close.addEventListener("click",function(evt){evt.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(evt){name.value&&email.value?isStorageSupport&&localStorage.setItem("name",name.value):(evt.preventDefault(),console.log("Нужно ввести имя и email"))}),window.addEventListener("keydown",function(evt){27===evt.keyCode&&(evt.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});